html:
{% block style %}
<style>
  .header-table {
    width: 100%;
    margin-bottom: 20px;
    border-collapse: collapse;
  }
  /* Celda izquierda sólo tan ancha como el logo */
  .header-table td:first-child {
    width: 1%;
    padding: 0;
    vertical-align: middle;
    text-align: left;     /* forzar alineación al margen */
    white-space: nowrap;   /* evitar que crezca más */
  }
  /* Celda derecha ocupa el resto y alinea su contenido a la derecha */
  .header-table td:last-child {
    width: auto;
    text-align: right;
    vertical-align: middle;
    padding: 0;
  }

  .logo {
    float: left;           /* empuja la imagen al borde izquierdo */
    height: 80px;          /* altura fija */
    width: auto !important;/* preserve aspect ratio */
    margin: 0;             /* quitar márgenes */
    object-fit: contain;
  }

  .info-table, .items-table, .totals-table {
    width: 100%;
    margin-bottom: 20px;
    border-collapse: collapse;
  }
  .info-table td { padding: 4px; }
  .items-table th, .items-table td { border: 1px solid #ccc; padding: 8px; }
  .text-right { text-align: right; }
  .totals-table td { padding: 4px; }
</style>
{% endblock %}

{% block body %}
<table class="header-table">
  <tr>
    <td style="width: 50%;"><img class="logo" src="/files/logo.jpg" alt="{{ company }}"></td>
    <td style="width: 50%; text-align: right;">
      <div class="header-title">FACTURA PROFORMA</div>
      <div>No. {{ doc.name }}</div>
    </td>
  </tr>
</table>

<table class="info-table">
  <tr>
    <td><strong>Fecha de emisión:</strong> {{ doc.date }}</td>
    <td><strong>Fecha de vencimiento:</strong> {{ doc.expiration_date }}</td>
  </tr>
  {% if doc.finance_application %}
  <tr>
    <td colspan="2"><strong>Aplicación de financiamiento:</strong> {{ doc.finance_application }}</td>
  </tr>
  {% endif %}
  {% if doc.amended_from %}
  <tr>
    <td colspan="2"><strong>Enmendada de:</strong> {{ doc.amended_from }}</td>
  </tr>
  {% endif %}
</table>

<table class="info-table">
  <tr>
    <td><strong>Cliente:</strong> {{ doc.customer }}</td>
    <td><strong>Teléfono:</strong> {{ doc.phone_number or '-' }}</td>
  </tr>
  <tr>
    <td colspan="2"><strong>Dirección:</strong> {{ doc.address or '-' }}</td>
  </tr>
</table>

<table class="items-table">
  <thead>
    <tr>
      <th>Ítem</th>
      <th>Cantidad</th>
      <th>Precio Unitario</th>
      <th>Total</th>
    </tr>
  </thead>
  <tbody>
    {% for row in doc.items %}
    <tr>
      <td>{{ row.item_name }}</td>
      <td class="text-right">{{ row.qty }}</td>
      <td class="text-right">{{ frappe.format(row.rate, "Currency") }}</td>
      <td class="text-right">{{ frappe.format(row.amount, "Currency") }}</td>
    </tr>
    {% endfor %}
  </tbody>
</table>

<table class="totals-table">
  <tr>
    <td style="width: 100;"></td>
    <td><strong>Subtotal:</strong></td>
    <td class="text-right">{{ frappe.format(doc.sub_total, "Currency") }}</td>
  </tr>
  {% if doc.discount and doc.discount > 0 %}
  <tr>
    <td></td>
    <td><strong>Descuento:</strong></td>
    <td class="text-right">{{ frappe.format(doc.discount, "Currency") }}</td>
  </tr>
  {% endif %}
  {% if doc.delivery_fee and doc.delivery_fee > 0 %}
  <tr>
    <td></td>
    <td><strong>Envío:</strong></td>
    <td class="text-right">{{ frappe.format(doc.delivery_fee, "Currency") }}</td>
  </tr>
  {% endif %}
  {% if doc.itbis and doc.itbis > 0 %}
  <tr>
    <td></td>
    <td><strong>ITBIS:</strong></td>
    <td class="text-right">{{ frappe.format(doc.itbis, "Currency") }}</td>
  </tr>
  {% endif %}
  <tr>
    <td></td>
    <td><strong>Total a pagar:</strong></td>
    <td class="text-right">{{ frappe.format(doc.grand_total, "Currency") }}</td>
  </tr>
  {% if doc.payment_method %}
  <tr>
    <td></td>
    <td><strong>Forma de pago:</strong></td>
    <td class="text-right">{{ doc.payment_method }}</td>
  </tr>
  {% endif %}
</table>
{% endblock %}
